import React from "react";
import ReactTextRotator from "react-text-rotator";
import dynamic from "next/dynamic";
const Background = dynamic(() =>
    import("components/landing/background/Clouds")
);

const content = [
    {
        text: '60% of women in the U.S are sexually unsatisfied',
        animation: 'fade',
    },
    {
        text: '31% of men suffer from some type of sexual dysfunction',
        animation: 'fade',
    },
    {
        text: '70% of divorces cite sex as one of the main reasons of divorce',
        animation: 'fade',
    }
];

export default function HeroStat() {
    return (
        <div className="container height--full-plus background--brand-type1--lv1">
            <div className="spacer height--lv11" />
            <div className="row">
                <div className="col size1of1 justify-content--center">
                    <div className="text text-align--center">
                        <h2 className="heading hero--lv2">
                            <ReactTextRotator
                                content={content}
                                time={2700}
                            />
                        </h2>
                    </div>
                </div>
            </div>
            <div className="spacer height--lv11" />
        </div>
    );
}
